import React, { Component } from 'react'
import { Link } from 'react-router-dom'

class Header extends Component {

    render() {
        return (
            <div>
                <header className="masthead" style={{ backgroundImage: this.props.backgroundImage }}>
                    <div className="container">
                        <div className="masthead-subheading">{this.props.title}</div>
                        <div className="masthead-heading text-uppercase">{this.props.subtitle}</div>
                        {this.props.showButton &&
                            <link className="btn btn-primary btn-xl text-uppercase js-scroll-trigger" to="/services">{this.props.buttonText} </link>
                        }

                    </div>
                </header>
            </div>
        )
    }


}

export default Header;
